import React, { useState, useEffect } from "react";

export default function Hero() {
  const images = [
    "https://img.freepik.com/free-vector/programmer-working-computer-flat-design_23-2148280176.jpg",
    "https://img.freepik.com/free-vector/web-development-programming-laptop-smartphone_107791-1529.jpg",
    "https://img.freepik.com/free-vector/ai-technology-brain-background-digital-transformation-concept_53876-112210.jpg",
    "https://img.freepik.com/free-vector/abstract-digital-technology-background_23-2148881329.jpg",
  ];

  const [currentImage, setCurrentImage] = useState(0);

  // Auto slide every 4 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prev) => (prev + 1) % images.length);
    }, 4000);
    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <section
      id="home"
      className="relative flex items-center justify-center min-h-screen text-white overflow-hidden"
    >
      {/* ===== Background Animated Video ===== */}
      <video
        autoPlay
        loop
        muted
        playsInline
        className="absolute inset-0 w-full h-full object-cover"
      >
        <source
          src="https://cdn.pixabay.com/vimeo/438511255/ai-48206.mp4?width=1280"
          type="video/mp4"
        />
      </video>

      {/* ===== Gradient Overlay for Clarity ===== */}
      <div className="absolute inset-0 bg-gradient-to-b from-black/80 via-black/60 to-black/90 backdrop-blur-sm"></div>

      {/* ===== Hero Content ===== */}
      <div className="relative z-10 container mx-auto px-6 py-20 grid md:grid-cols-2 gap-12 items-center">
        {/* LEFT SIDE CONTENT */}
        <div className="animate-fadeInUp">
          <p className="text-cyan-400 font-semibold mb-3 uppercase tracking-wider">
            Welcome to Neotech
          </p>
          <h1 className="text-5xl md:text-6xl font-extrabold mb-6 leading-tight">
            <span className="text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 to-blue-600 animate-pulse">
              Building Next-Gen Software
            </span>
            <br />
            that Empowers Innovation ðŸš€
          </h1>
          <p className="text-gray-300 mb-8 leading-relaxed max-w-md">
            We turn ideas into cutting-edge digital experiences. Our expertise
            in software, AI, and cloud empowers your business to reach the
            future â€” faster.
          </p>

          <div className="flex gap-5">
            <a
              href="#contact"
              className="px-8 py-3 bg-gradient-to-r from-cyan-500 to-blue-600 rounded-full font-semibold shadow-lg shadow-cyan-500/30 hover:shadow-blue-600/40 hover:scale-105 transition-transform duration-300"
            >
              Get Started
            </a>
            <a
              href="#services"
              className="px-8 py-3 border border-cyan-400/50 rounded-full text-cyan-300 hover:bg-cyan-500/20 hover:border-cyan-400 hover:scale-105 transition-all duration-300"
            >
              View Services
            </a>
          </div>
        </div>

        {/* RIGHT SIDE SLIDESHOW */}
        <div className="relative flex justify-center items-center">
          <div className="absolute w-72 h-72 bg-cyan-500/20 rounded-full blur-3xl animate-pulse"></div>

          {/* Image Slideshow */}
          {images.map((img, index) => (
            <img
              key={index}
              src={img}
              alt={`Slide ${index + 1}`}
              className={`w-[90%] md:w-full max-w-md rounded-3xl shadow-[0_0_50px_rgba(0,200,255,0.4)] absolute transition-opacity duration-1000 ease-in-out ${
                index === currentImage ? "opacity-100 z-10 scale-105" : "opacity-0"
              }`}
            />
          ))}
        </div>
      </div>

      {/* ===== Floating Elements for Animation ===== */}
      <div className="absolute top-10 left-10 w-16 h-16 bg-cyan-500/40 rounded-full blur-2xl animate-ping"></div>
      <div className="absolute bottom-10 right-10 w-24 h-24 bg-blue-600/30 rounded-full blur-2xl animate-pulse"></div>
    </section>
  );
}
