import React, { useState, useEffect } from "react";

export default function Header() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [theme, setTheme] = useState("light");

  // Apply saved theme
  useEffect(() => {
    const savedTheme = localStorage.getItem("theme") || "light";
    setTheme(savedTheme);
    document.documentElement.classList.toggle("dark", savedTheme === "dark");
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);
    document.documentElement.classList.toggle("dark", newTheme === "dark");
    localStorage.setItem("theme", newTheme);
  };

  return (
    <header className="fixed top-0 w-full z-50 bg-white/80 dark:bg-gray-900/80 backdrop-blur-md shadow-lg border-b border-gray-200 dark:border-gray-700 transition-all duration-500">
      <div className="max-w-7xl mx-auto px-6 py-3 flex items-center justify-between text-gray-900 dark:text-white">

        {/* ===== LEFT: LOGO ===== */}
        <div className="flex items-center gap-3 cursor-pointer group">
          <div className="w-11 h-11 rounded-xl bg-gradient-to-br from-cyan-400 via-indigo-500 to-purple-600 
          flex items-center justify-center text-white font-extrabold text-lg shadow-md 
          transform transition-transform duration-500 group-hover:rotate-180 group-hover:scale-110">
            N
          </div>
          <div>
            <div className="text-xl font-extrabold tracking-wide">Neotech</div>
            <div className="text-xs text-gray-500 dark:text-gray-400">Software & Cloud</div>
          </div>
        </div>

        {/* ===== CENTER: NAVIGATION ===== */}
        <nav className="hidden md:flex items-center gap-10 text-sm font-medium">
          {["Home", "Services", "About"].map((item) => (
            <a
              key={item}
              href={`#${item.toLowerCase()}`}
              className="relative group hover:text-cyan-400 transition-all duration-300"
            >
              {item}
              <span className="absolute left-0 -bottom-1 w-0 h-[2px] bg-cyan-400 group-hover:w-full transition-all duration-300"></span>
            </a>
          ))}
        </nav>

        {/* ===== RIGHT: CONTACT + THEME + MENU ===== */}
        <div className="flex items-center gap-4">
          {/* Contact Button */}
          <a
            href="#contact"
            className="hidden md:inline-block px-5 py-2 rounded-full bg-gradient-to-r from-cyan-500 to-indigo-600 
            text-white text-sm font-semibold shadow-md hover:shadow-cyan-500/30 hover:scale-105 transition-transform duration-300"
          >
            Contact Us
          </a>

          {/* Dark Mode Toggle */}
          <button
            onClick={toggleTheme}
            className="p-2 rounded-full bg-gray-200 dark:bg-gray-800 text-gray-900 dark:text-white 
            shadow hover:scale-110 transition-transform duration-300"
            title="Toggle Theme"
          >
            {theme === "light" ? "üåô" : "‚òÄÔ∏è"}
          </button>

          {/* Mobile Menu Toggle */}
          <button
            className="md:hidden flex flex-col gap-1.5 focus:outline-none"
            onClick={() => setMenuOpen(!menuOpen)}
          >
            <span
              className={`block w-6 h-0.5 bg-gray-900 dark:bg-white transition-all duration-300 ${
                menuOpen ? "rotate-45 translate-y-2" : ""
              }`}
            ></span>
            <span
              className={`block w-6 h-0.5 bg-gray-900 dark:bg-white transition-all duration-300 ${
                menuOpen ? "opacity-0" : ""
              }`}
            ></span>
            <span
              className={`block w-6 h-0.5 bg-gray-900 dark:bg-white transition-all duration-300 ${
                menuOpen ? "-rotate-45 -translate-y-2" : ""
              }`}
            ></span>
          </button>
        </div>
      </div>

      {/* ===== MOBILE MENU ===== */}
      {menuOpen && (
        <div className="md:hidden bg-white/95 dark:bg-gray-900/95 backdrop-blur-md text-center text-gray-900 dark:text-white border-t border-gray-200 dark:border-gray-700">
          {["Home", "Services", "About", "Contact"].map((item) => (
            <a
              key={item}
              href={`#${item.toLowerCase()}`}
              className="block py-3 border-b border-gray-200 dark:border-gray-700 hover:bg-gray-100 dark:hover:bg-gray-800 transition"
              onClick={() => setMenuOpen(false)}
            >
              {item}
            </a>
          ))}
        </div>
      )}
    </header>
  );
}
