import React from "react";
import { motion } from "framer-motion";

const services = [
  {
    title: "Web Development",
    desc: "We build responsive, modern, and high-performance web apps.",
    icon: "ðŸ’»",
  },
  {
    title: "Mobile Apps",
    desc: "Beautiful, cross-platform mobile experiences for iOS & Android.",
    icon: "ðŸ“±",
  },
  {
    title: "AI & Automation",
    desc: "Integrate AI and automate workflows with intelligent solutions.",
    icon: "ðŸ¤–",
  },
];

export default function Services() {
  return (
    <section id="services" className="py-24 bg-gradient-to-b from-white to-blue-50">
      <div className="max-w-6xl mx-auto px-6 text-center">
        <h2 className="text-4xl font-bold text-gray-800 mb-10">
          Our <span className="text-blue-500">Services</span>
        </h2>
        <div className="grid md:grid-cols-3 gap-10">
          {services.map((s, i) => (
            <motion.div
              key={i}
              whileHover={{ scale: 1.05 }}
              className="p-8 bg-white rounded-2xl shadow-lg border border-blue-100 hover:shadow-2xl transition-all"
            >
              <div className="text-5xl">{s.icon}</div>
              <h3 className="text-2xl font-semibold mt-4 mb-2">{s.title}</h3>
              <p className="text-gray-600">{s.desc}</p>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
}
