import React from "react";

function Header() {
  return (
    <header
      style={{
        position: "relative",
        width: "100%",
        height: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        color: "#fff",
        textAlign: "center",
        fontFamily: "'Poppins', sans-serif",
      }}
    >
      {/* ✅ Working Video Background */}
      <video
        src="https://videos.pexels.com/video-files/854331/854331-hd_1920_1080_30fps.mp4"
        autoPlay
        muted
        loop
        playsInline
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          objectFit: "cover",
          zIndex: "-2",
        }}
      />

      {/* Dark overlay */}
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          background:
            "linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.7))",
          zIndex: "-1",
        }}
      ></div>

      {/* ✅ Working Image (NeoTect Logo / Placeholder) */}
      <img
        src="https://cdn-icons-png.flaticon.com/512/906/906361.png"
        alt="NeoTect Logo"
        style={{
          width: "120px",
          marginBottom: "20px",
          filter: "drop-shadow(0 0 15px #00ffff)",
          animation: "float 3s ease-in-out infinite",
        }}
      />

      {/* Website Title */}
      <h1
        style={{
          fontSize: "3.2rem",
          fontWeight: "700",
          letterSpacing: "1px",
          marginBottom: "15px",
          textShadow: "0px 0px 15px #00ffff",
        }}
      >
        Welcome to{" "}
        <span style={{ color: "#00ffff", fontWeight: "800" }}>NeoTect.com</span>
      </h1>

      <p
        style={{
          fontSize: "1.2rem",
          maxWidth: "600px",
          color: "#dcdcdc",
          marginBottom: "40px",
        }}
      >
        Empowering innovation through open-source technology and creative design.
      </p>

      {/* Buttons */}
      <div
        style={{
          display: "flex",
          gap: "25px",
          justifyContent: "center",
          flexWrap: "wrap",
        }}
      >
        <button
          style={{
            background: "linear-gradient(90deg, #00ffff, #007bff)",
            border: "none",
            color: "#fff",
            padding: "14px 34px",
            borderRadius: "40px",
            fontSize: "1rem",
            fontWeight: "600",
            cursor: "pointer",
            boxShadow: "0 0 15px #00ffff",
            transition: "all 0.3s ease",
          }}
          onMouseOver={(e) => {
            e.target.style.boxShadow = "0 0 25px #00ffff";
            e.target.style.transform = "scale(1.05)";
          }}
          onMouseOut={(e) => {
            e.target.style.boxShadow = "0 0 15px #00ffff";
            e.target.style.transform = "scale(1)";
          }}
        >
          Sign Up
        </button>

        <button
          style={{
            background: "transparent",
            border: "2px solid #00ffff",
            color: "#00ffff",
            padding: "14px 34px",
            borderRadius: "40px",
            fontSize: "1rem",
            fontWeight: "600",
            cursor: "pointer",
            boxShadow: "0 0 10px #00ffff",
            transition: "all 0.3s ease",
          }}
          onMouseOver={(e) => {
            e.target.style.background = "#00ffff";
            e.target.style.color = "#000";
            e.target.style.transform = "scale(1.05)";
          }}
          onMouseOut={(e) => {
            e.target.style.background = "transparent";
            e.target.style.color = "#00ffff";
            e.target.style.transform = "scale(1)";
          }}
        >
          Log In
        </button>
      </div>

      {/* Floating animation */}
      <style>{`
        @keyframes float {
          0% { transform: translateY(0px); }
          50% { transform: translateY(-10px); }
          100% { transform: translateY(0px); }
        }

        @media (max-width: 768px) {
          h1 { font-size: 2rem !important; }
          p { font-size: 1rem !important; padding: 0 15px; }
          button { width: 80%; }
        }
      `}</style>
    </header>
  );
}

export default Header;
