import React from "react";
import { motion } from "framer-motion";
import { Facebook, Twitter, Linkedin, Github } from "lucide-react";

export default function Footer() {
  return (
    <footer className="bg-gray-950 text-gray-300 py-10 border-t border-cyan-500/20">
      <div className="max-w-6xl mx-auto px-6 md:px-12 flex flex-col md:flex-row justify-between items-center gap-6">
        {/* Left - Logo & Text */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="text-center md:text-left"
        >
          <h3 className="text-2xl font-bold text-cyan-400">Neotech</h3>
          <p className="text-gray-400 text-sm mt-2">
            Â© {new Date().getFullYear()} Neotech Software Pvt. Ltd.  
            <br /> Empowering Innovation Globally.
          </p>
        </motion.div>

        {/* Right - Socials */}
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="flex gap-5"
        >
          {[Facebook, Twitter, Linkedin, Github].map((Icon, i) => (
            <a
              key={i}
              href="#"
              className="p-3 rounded-full bg-gray-800 hover:bg-cyan-600 transition-all"
            >
              <Icon size={18} />
            </a>
          ))}
        </motion.div>
      </div>
    </footer>
  );
}
