import React, { useState, useEffect } from "react";

export default function Hero() {
  // Modern, high-quality software-related images
  const images = [
    "https://img.freepik.com/free-photo/futuristic-digital-world-map-hologram_23-2150901392.jpg",
    "https://img.freepik.com/free-photo/developer-typing-laptop-computer-with-code-screen-software-programming-web-development-coding_236854-31628.jpg",
    "https://img.freepik.com/free-photo/ai-cloud-concept-with-robot-hand_23-2149738557.jpg",
    "https://img.freepik.com/free-photo/software-developer-using-laptop-computer-with-code_236854-29607.jpg",
    "https://img.freepik.com/free-photo/digital-transformation-technology-background-with-hexagons_53876-97406.jpg",
  ];

  const [currentImage, setCurrentImage] = useState(0);

  // Auto-change image every 4 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prev) => (prev + 1) % images.length);
    }, 4000);
    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <section
      id="home"
      className="relative flex items-center justify-center min-h-screen text-white overflow-hidden"
    >
      {/* ===== Background Animated Video ===== */}
      <video
        autoPlay
        loop
        muted
        playsInline
        className="absolute inset-0 w-full h-full object-cover"
      >
        <source
          src="https://cdn.pixabay.com/vimeo/438511255/ai-48206.mp4?width=1280"
          type="video/mp4"
        />
      </video>

      {/* ===== Gradient Overlay for Clarity ===== */}
      <div className="absolute inset-0 bg-gradient-to-b from-black/90 via-black/70 to-black/95 backdrop-blur-sm"></div>

      {/* ===== Hero Main Content ===== */}
      <div className="relative z-10 container mx-auto px-6 py-20 grid md:grid-cols-2 gap-16 items-center">
        {/* ===== LEFT SIDE CONTENT ===== */}
        <div className="space-y-6 animate-fadeInUp">
          <p className="text-cyan-400 font-semibold uppercase tracking-widest text-sm md:text-base">
            Welcome to <span className="text-blue-400">Neotech</span>
          </p>
          <h1 className="text-5xl md:text-6xl font-extrabold leading-tight">
            <span className="text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 via-blue-500 to-indigo-600 animate-pulse">
              Empowering Businesses
            </span>
            <br />
            with Next-Gen Software Solutions 🚀
          </h1>
          <p className="text-gray-300 text-lg leading-relaxed max-w-md">
            We design and develop intelligent, scalable software systems powered
            by innovation, cloud, and AI — helping your business move at the
            speed of technology.
          </p>

          <div className="flex flex-wrap gap-4 pt-4">
            <a
              href="#contact"
              className="px-8 py-3 bg-gradient-to-r from-cyan-500 to-blue-600 rounded-full font-semibold shadow-lg shadow-cyan-500/40 hover:shadow-blue-600/40 hover:scale-105 transition-transform duration-300"
            >
              Get Started
            </a>
            <a
              href="#services"
              className="px-8 py-3 border border-cyan-400/60 rounded-full text-cyan-300 hover:bg-cyan-500/20 hover:border-cyan-400 hover:scale-105 transition-all duration-300"
            >
              Explore Services
            </a>
          </div>
        </div>

        {/* ===== RIGHT SIDE IMAGE SLIDESHOW ===== */}
        <div className="relative flex justify-center items-center">
          <div className="absolute w-80 h-80 bg-cyan-500/20 rounded-full blur-3xl animate-pulse"></div>

          {images.map((img, index) => (
            <img
              key={index}
              src={img}
              alt={`Slide ${index + 1}`}
              className={`w-[90%] md:w-full max-w-lg rounded-3xl shadow-[0_0_50px_rgba(0,200,255,0.3)] absolute transition-opacity duration-[1200ms] ease-in-out ${
                index === currentImage ? "opacity-100 scale-105 z-10" : "opacity-0 scale-95"
              }`}
            />
          ))}

          {/* Small Dot Indicators */}
          <div className="absolute bottom-4 flex gap-2">
            {images.map((_, i) => (
              <div
                key={i}
                className={`w-3 h-3 rounded-full transition-all duration-300 ${
                  i === currentImage
                    ? "bg-cyan-400 scale-125"
                    : "bg-white/30 hover:bg-cyan-300/50"
                }`}
              ></div>
            ))}
          </div>
        </div>
      </div>

      {/* ===== Floating Decorative Elements ===== */}
      <div className="absolute top-10 left-10 w-16 h-16 bg-cyan-400/40 rounded-full blur-2xl animate-ping"></div>
      <div className="absolute bottom-10 right-10 w-24 h-24 bg-blue-600/30 rounded-full blur-2xl animate-pulse"></div>
    </section>
  );
}
