import React, { useState, useEffect } from "react";
import { ChevronLeft, ChevronRight } from "lucide-react";

export default function Hero() {
  const images = [
    "https://img.freepik.com/free-photo/developer-coding-laptop_1098-18707.jpg",
    "https://img.freepik.com/free-photo/futuristic-digital-technology-background_53876-104051.jpg",
    "https://img.freepik.com/free-photo/team-software-developers-working-together_482257-23762.jpg",
  ];

  const [current, setCurrent] = useState(0);

  const nextSlide = () => setCurrent((prev) => (prev + 1) % images.length);
  const prevSlide = () => setCurrent((prev) => (prev - 1 + images.length) % images.length);

  useEffect(() => {
    const interval = setInterval(nextSlide, 4000); // auto-slide every 4s
    return () => clearInterval(interval);
  }, []);

  return (
    <section
      id="home"
      className="relative min-h-screen flex flex-col md:flex-row items-center justify-between bg-gray-50 pt-24 overflow-hidden"
    >
      {/* LEFT TEXT CONTENT */}
      <div className="w-full md:w-1/2 px-10 md:px-16">
        <h2 className="text-4xl md:text-6xl font-extrabold leading-tight text-gray-900">
          Empowering Businesses
          <br />
          <span className="text-transparent bg-clip-text bg-gradient-to-r from-blue-500 to-cyan-400">
            With Modern Solutions
          </span>
        </h2>
        <p className="text-gray-600 mt-6 text-lg leading-relaxed max-w-lg">
          Build fast, scalable, and modern web solutions that take your business to the next level.
        </p>

        <div className="mt-8 flex flex-wrap gap-4">
          <a
            href="#contact"
            className="px-8 py-3 bg-gradient-to-r from-blue-500 to-cyan-500 text-white font-semibold rounded-full shadow-md hover:scale-105 transition-transform"
          >
            Get Started
          </a>
          <a
            href="#services"
            className="px-8 py-3 border border-blue-400 text-blue-500 rounded-full hover:bg-blue-50 hover:scale-105 transition-transform"
          >
            Explore Services
          </a>
        </div>
      </div>

      {/* RIGHT IMAGE SLIDER */}
      <div className="relative w-full md:w-1/2 flex justify-center items-center px-8 md:px-0 mt-10 md:mt-0">
        <div className="relative w-[90%] h-[420px] md:h-[480px] overflow-hidden rounded-3xl shadow-2xl">
          {images.map((img, i) => (
            <img
              key={i}
              src={img}
              alt={`Slide ${i + 1}`}
              className={`absolute inset-0 w-full h-full object-cover transition-opacity duration-[1200ms] ease-in-out ${
                i === current ? "opacity-100" : "opacity-0"
              }`}
            />
          ))}
        </div>

        {/* Slide Controls */}
        <button
          onClick={prevSlide}
          className="absolute left-6 bg-black/40 hover:bg-blue-500/60 text-white p-3 rounded-full transition-all"
        >
          <ChevronLeft size={24} />
        </button>
        <button
          onClick={nextSlide}
          className="absolute right-6 bg-black/40 hover:bg-blue-500/60 text-white p-3 rounded-full transition-all"
        >
          <ChevronRight size={24} />
        </button>

        {/* Dots */}
        <div className="absolute bottom-6 flex gap-3">
          {images.map((_, i) => (
            <span
              key={i}
              onClick={() => setCurrent(i)}
              className={`w-3.5 h-3.5 rounded-full cursor-pointer transition-all ${
                i === current ? "bg-blue-500 scale-125" : "bg-gray-300 hover:bg-blue-400"
              }`}
            ></span>
          ))}
        </div>
      </div>
    </section>
  );
}
