import React, { useState } from "react";
import axios from "axios";

export default function Contact() {
  const [form, setForm] = useState({ name: "", email: "", message: "" });
  const [status, setStatus] = useState("");

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("Sending...");
    try {
      await axios.post("http://localhost:5000/api/contact", form);
      setStatus("✅ Message Sent!");
      setForm({ name: "", email: "", message: "" });
    } catch {
      setStatus("❌ Error sending message.");
    }
  };

  return (
    <section id="contact" className="py-20 bg-gradient-to-b from-slate-800 to-slate-900">
      <div className="max-w-5xl mx-auto px-8 grid md:grid-cols-2 gap-12 items-center">
        <div>
          <h2 className="text-3xl font-bold mb-4 text-white">Get in Touch</h2>
          <p className="text-gray-300 mb-6">
            Have a project or collaboration in mind? Let's make it happen.
          </p>
        </div>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input name="name" value={form.name} onChange={handleChange} placeholder="Your Name" className="w-full p-3 rounded-md bg-white/10 text-white outline-none" required />
          <input name="email" value={form.email} onChange={handleChange} placeholder="Your Email" className="w-full p-3 rounded-md bg-white/10 text-white outline-none" required />
          <textarea name="message" value={form.message} onChange={handleChange} placeholder="Message" rows="5" className="w-full p-3 rounded-md bg-white/10 text-white outline-none" required />
          <button type="submit" className="px-6 py-3 bg-gradient-to-r from-cyan-500 to-blue-600 rounded-full font-semibold text-white hover:scale-105 transition">
            Send Message
          </button>
          <p className="text-sm text-cyan-300 mt-2">{status}</p>
        </form>
      </div>
    </section>
  );
}
