import React from "react";
import { motion } from "framer-motion";
import { Facebook, Twitter, Linkedin, Mail } from "lucide-react";

export default function Footer() {
  return (
    <footer className="bg-gradient-to-r from-gray-900 via-gray-800 to-gray-900 text-white py-10 border-t border-gray-700">
      <div className="max-w-7xl mx-auto px-6 flex flex-col md:flex-row items-center justify-between gap-6">

        {/* === LEFT: LOGO === */}
        <motion.div
          initial={{ opacity: 0, x: -30 }}
          whileInView={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
          className="flex items-center gap-3"
        >
          <div className="w-10 h-10 bg-gradient-to-r from-blue-500 to-cyan-400 rounded-full flex items-center justify-center text-xl font-bold shadow-lg">
            N
          </div>
          <h1 className="text-2xl font-semibold tracking-wide">NeoTech</h1>
        </motion.div>

        {/* === CENTER: NAV LINKS === */}
        <motion.div
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="flex flex-wrap justify-center gap-6 text-gray-300 text-sm"
        >
          <a href="#home" className="hover:text-cyan-400 transition-all">Home</a>
          <a href="#about" className="hover:text-cyan-400 transition-all">About</a>
          <a href="#services" className="hover:text-cyan-400 transition-all">Services</a>
          <a href="#contact" className="hover:text-cyan-400 transition-all">Contact</a>
        </motion.div>

        {/* === RIGHT: SOCIAL ICONS === */}
        <motion.div
          initial={{ opacity: 0, x: 30 }}
          whileInView={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
          className="flex items-center gap-4 text-gray-400"
        >
          <a
            href="https://facebook.com"
            target="_blank"
            rel="noopener noreferrer"
            className="hover:text-blue-500 transition-all"
          >
            <Facebook size={20} />
          </a>
          <a
            href="https://twitter.com"
            target="_blank"
            rel="noopener noreferrer"
            className="hover:text-cyan-400 transition-all"
          >
            <Twitter size={20} />
          </a>
          <a
            href="https://linkedin.com"
            target="_blank"
            rel="noopener noreferrer"
            className="hover:text-blue-400 transition-all"
          >
            <Linkedin size={20} />
          </a>
          <a
            href="mailto:info@neotech.com"
            className="hover:text-cyan-300 transition-all"
          >
            <Mail size={20} />
          </a>
        </motion.div>
      </div>

      {/* === BOTTOM COPYRIGHT === */}
      <div className="mt-8 text-center text-gray-500 text-sm border-t border-gray-700 pt-6">
        Â© {new Date().getFullYear()} NeoTech Solutions. All rights reserved.
      </div>
    </footer>
  );
}
