import React from "react";
import { motion } from "framer-motion";

export default function About() {
  return (
    <section
      id="about"
      className="relative py-24 bg-gradient-to-b from-black via-gray-900 to-gray-950 text-white overflow-hidden"
    >
      <div className="absolute inset-0 bg-[radial-gradient(circle_at_bottom_right,_rgba(14,165,233,0.1),_transparent_60%)]" />
      <div className="relative max-w-6xl mx-auto px-6 z-10 text-center md:text-left">
        <motion.h2
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="text-4xl md:text-5xl font-bold mb-10 text-center"
        >
          About <span className="bg-gradient-to-r from-cyan-400 to-blue-500 text-transparent bg-clip-text">Neotech</span>
        </motion.h2>

        <div className="grid md:grid-cols-2 gap-10 items-center">
          <motion.div
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            className="text-gray-300 leading-relaxed"
          >
            <p className="mb-4">
              At <span className="text-cyan-400 font-semibold">Neotech</span>, we are passionate about
              transforming ideas into digital reality. Our team of innovators,
              developers, and designers collaborates to craft powerful web,
              mobile, and AI-driven solutions.
            </p>
            <p>
              We specialize in modern technologies like <strong>React, Node.js, AI, and Cloud</strong> â€”
              helping businesses achieve scalability, automation, and global reach.
              Our mission is to empower innovation and deliver unmatched digital excellence.
            </p>
          </motion.div>

          <motion.img
            src="https://cdn.pixabay.com/photo/2017/06/29/00/05/startup-2452805_1280.jpg"
            alt="About Neotech"
            initial={{ opacity: 0, x: 50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            className="rounded-2xl shadow-lg shadow-cyan-500/20 border border-cyan-500/10"
          />
        </div>
      </div>
    </section>
  );
}
