import React from "react";
import { motion } from "framer-motion";
import { Facebook, Instagram, Linkedin, Mail, Phone } from "lucide-react";

export default function Footer() {
  return (
    <footer className="relative bg-gradient-to-b from-gray-900 via-gray-950 to-black text-gray-300 py-16 border-t border-cyan-500/20">
      <div className="max-w-7xl mx-auto px-6 md:px-12 grid md:grid-cols-3 gap-12">

        {/* ===== COLUMN 1: LOGO & ABOUT ===== */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          <div className="flex items-center gap-3 mb-4">
            <div className="w-12 h-12 rounded-xl bg-gradient-to-br from-cyan-400 via-blue-500 to-indigo-600 flex items-center justify-center text-white font-extrabold text-xl shadow-lg">
              N
            </div>
            <h3 className="text-2xl font-bold text-white">NeoTech</h3>
          </div>
          <p className="text-gray-400 text-sm leading-relaxed">
            Crafting cutting-edge web, mobile, and AI-powered solutions that
            drive innovation and business growth.  
          </p>
        </motion.div>

        {/* ===== COLUMN 2: QUICK LINKS ===== */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 1.2 }}
          className="text-center md:text-left"
        >
          <h4 className="text-lg font-semibold text-cyan-400 mb-4">Quick Links</h4>
          <ul className="space-y-2">
            {["Home", "About", "Services", "Contact"].map((link) => (
              <li key={link}>
                <a
                  href={`#${link.toLowerCase()}`}
                  className="hover:text-cyan-400 transition-colors duration-300"
                >
                  {link}
                </a>
              </li>
            ))}
          </ul>
        </motion.div>

        {/* ===== COLUMN 3: CONTACT INFO ===== */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 1.4 }}
          className="text-center md:text-left"
        >
          <h4 className="text-lg font-semibold text-cyan-400 mb-4">Get in Touch</h4>
          <p className="flex items-center justify-center md:justify-start gap-2">
            <Mail size={18} className="text-cyan-400" /> info@neotech.com
          </p>
          <p className="flex items-center justify-center md:justify-start gap-2 mt-2">
            <Phone size={18} className="text-cyan-400" /> +91 98765 43210
          </p>
          <div className="flex justify-center md:justify-start gap-4 mt-5">
            <a href="#" className="hover:text-cyan-400 transition-all duration-300">
              <Facebook size={22} />
            </a>
            <a href="#" className="hover:text-cyan-400 transition-all duration-300">
              <Instagram size={22} />
            </a>
            <a href="#" className="hover:text-cyan-400 transition-all duration-300">
              <Linkedin size={22} />
            </a>
          </div>
        </motion.div>
      </div>

      {/* ===== COPYRIGHT SECTION ===== */}
      <div className="border-t border-gray-800 mt-12 pt-6 text-center text-gray-500 text-sm">
        Â© {new Date().getFullYear()} <span className="text-cyan-400 font-semibold">NeoTech</span>. All rights reserved.
      </div>

      {/* ===== GLOW EFFECT ===== */}
      <div className="absolute inset-x-0 bottom-0 h-[3px] bg-gradient-to-r from-cyan-400 via-blue-500 to-indigo-600 blur-sm" />
    </footer>
  );
}
