import React, { useState } from "react";
import { ChevronLeft, ChevronRight } from "lucide-react";

export default function Hero() {
  const images = [
    "https://images.unsplash.com/photo-1522071820081-009f0129c71c?q=80&w=1200&auto=format&fit=crop&ixlib=rb-4.0.3&s=a1b3f0c9b5b6c2f2c3d8f3f9b8e1d4e2",
    "https://images.unsplash.com/photo-1555066931-4365d14bab8c?q=80&w=1200&auto=format&fit=crop&ixlib=rb-4.0.3&s=6c8e67ef1e3d60a3b2b5a1f65e7b8a6f",
    "https://images.unsplash.com/photo-1557693306-9f3f6f6c7a3d?q=80&w=1200&auto=format&fit=crop&ixlib=rb-4.0.3&s=9c7b4d2d8b2e2a8a9c6f2d5b8f3a1d3b"
  ];

  const [current, setCurrent] = useState(0);

  const next = () => setCurrent((p) => (p + 1) % images.length);
  const prev = () => setCurrent((p) => (p - 1 + images.length) % images.length);

  return (
    <section id="home" className="min-h-screen flex items-center bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 text-white">
      <div className="max-w-7xl mx-auto px-8 md:px-12 grid md:grid-cols-2 gap-12 items-center py-24">
        {/* left content aligned to header padding */}
        <div className="order-2 md:order-1">
          <p className="text-sm uppercase text-cyan-400 font-semibold mb-4">Welcome to Neotech</p>
          <h1 className="text-4xl md:text-5xl font-extrabold leading-tight mb-6">
            Building modern software â€” <span className="text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 to-blue-400">fast & secure</span>
          </h1>
          <p className="text-gray-300 max-w-xl mb-8">We create cloud-native, scalable applications and delightful user experiences. Full-stack dev, cloud infra, and AI automation.</p>

          <div className="flex gap-4 flex-wrap">
            <a href="#contact" className="px-6 py-3 bg-gradient-to-r from-cyan-500 to-blue-600 rounded-full font-semibold shadow">Get Started</a>
            <a href="#services" className="px-6 py-3 border border-white/20 rounded-full text-white/90">Our Services</a>
          </div>
        </div>

        {/* right slideshow */}
        <div className="order-1 md:order-2 flex justify-center">
          <div className="relative w-full max-w-lg rounded-2xl overflow-hidden shadow-2xl">
            {images.map((src, i) => (
              <img key={i}
                src={src}
                alt={`slide-${i}`}
                className={`w-full h-80 md:h-[480px] object-cover transition-opacity duration-700 ${i === current ? "opacity-100" : "opacity-0 absolute inset-0"}`}
              />
            ))}

            <button onClick={prev} className="absolute left-4 top-1/2 -translate-y-1/2 bg-black/40 p-2 rounded-full hover:bg-cyan-500/70">
              <ChevronLeft size={20} />
            </button>
            <button onClick={next} className="absolute right-4 top-1/2 -translate-y-1/2 bg-black/40 p-2 rounded-full hover:bg-cyan-500/70">
              <ChevronRight size={20} />
            </button>

            <div className="absolute bottom-4 left-1/2 -translate-x-1/2 flex gap-2">
              {images.map((_, i) => (
                <button key={i} onClick={() => setCurrent(i)} className={`w-3 h-3 rounded-full ${i === current ? "bg-cyan-400 scale-125" : "bg-white/40"}`}></button>
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}
