import React from "react";

export default function Hero() {
  return (
    <section id="home" className="flex flex-col items-center justify-center text-center py-28 md:py-36 px-6 bg-gradient-to-b from-transparent to-slate-900">
      <h1 className="text-4xl md:text-6xl font-extrabold text-white mb-6 leading-tight">
        Empowering the <span className="text-cyan-400">Digital Future</span>
      </h1>
      <p className="text-gray-300 max-w-2xl mx-auto mb-8 text-lg">
        We craft scalable, modern web solutions for startups and enterprises using cutting-edge technology.
      </p>
      <div className="flex gap-4 justify-center">
        <a href="#services" className="px-6 py-3 rounded-full bg-gradient-to-r from-cyan-500 to-blue-600 font-semibold text-white shadow hover:scale-105 transition">Our Services</a>
        <a href="#contact" className="px-6 py-3 rounded-full border border-cyan-400 text-cyan-300 hover:bg-cyan-500/10 transition">Get In Touch</a>
      </div>
    </section>
  );
}
