import React from "react";

const data = [
  { title: "Web Development", desc: "Modern, scalable web apps with React and Node.js." },
  { title: "Cloud Solutions", desc: "Deploy and scale with AWS, Azure, and Docker." },
  { title: "API Integration", desc: "Fast and secure RESTful and GraphQL APIs." },
  { title: "Mobile Apps", desc: "Cross-platform apps built with React Native." },
  { title: "AI & Automation", desc: "Integrating AI to automate your workflows." },
  { title: "UI/UX Design", desc: "Clean, user-centric design for digital experiences." },
];

export default function Services() {
  return (
    <section id="services" className="py-20 bg-slate-800/40">
      <div className="max-w-7xl mx-auto px-8 text-center">
        <h2 className="text-3xl md:text-4xl font-bold mb-10 text-white">Our Services</h2>
        <div className="grid gap-8 sm:grid-cols-2 lg:grid-cols-3">
          {data.map((service, i) => (
            <div
              key={i}
              className="p-8 rounded-2xl bg-white/5 backdrop-blur-md hover:bg-white/10 shadow-lg transition-all"
            >
              <h3 className="text-xl font-semibold text-cyan-400 mb-3">{service.title}</h3>
              <p className="text-gray-300 text-sm">{service.desc}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
