import React from "react";
import Card from "./Card";

function Services() {
  return (
    <div
      className="container services"
      style={{
        background: "radial-gradient(circle at top, #0b0b0b 0%, #000 80%)",
        color: "#fff",
        textAlign: "center",
        padding: "80px 20px",
        fontFamily: "'Poppins', sans-serif",
      }}
    >
      {/* Section Title */}
      <h2
        className="main-title"
        style={{
          fontSize: "2.8rem",
          fontWeight: "700",
          letterSpacing: "1px",
          marginBottom: "50px",
          color: "#00ffff",
          textShadow: "0 0 15px #00ffff",
        }}
      >
        SERVICES
      </h2>

      {/* Cards Wrapper */}
      <div
        className="card-cover"
        style={{
          display: "flex",
          justifyContent: "center",
          flexWrap: "wrap",
          gap: "40px",
        }}
      >
        {/* Card 1 */}
        <Card
          title="Web Development"
          img="https://cdn.pixabay.com/photo/2017/06/10/07/18/web-2389258_1280.jpg"
          text="Building responsive, modern, and scalable web applications using the latest frameworks and technologies."
        />

        {/* Card 2 */}
        <Card
          title="E-Commerce Services"
          img="https://cdn.pixabay.com/photo/2018/03/29/11/53/ecommerce-3271742_1280.jpg"
          text="Delivering complete e-commerce solutions including custom storefronts, payment integration, and analytics."
        />

        {/* Card 3 */}
        <Card
          title="Cyber Security"
          img="https://cdn.pixabay.com/photo/2016/11/19/14/00/monitor-1838330_1280.jpg"
          text="Protecting your digital assets through advanced security auditing, penetration testing, and threat prevention."
        />
      </div>

      {/* Neon glow animation */}
      <style>{`
        .card-cover .card {
          background: rgba(255, 255, 255, 0.05);
          border-radius: 20px;
          box-shadow: 0 0 20px rgba(0, 255, 255, 0.3);
          overflow: hidden;
          width: 320px;
          transition: all 0.4s ease;
          backdrop-filter: blur(10px);
        }

        .card-cover .card:hover {
          transform: translateY(-10px) scale(1.03);
          box-shadow: 0 0 35px rgba(0, 255, 255, 0.6);
        }

        .card-cover .card img {
          width: 100%;
          height: 200px;
          object-fit: cover;
          border-bottom: 2px solid #00ffff;
        }

        .card-cover .card-body {
          padding: 25px 20px;
        }

        .card-cover .card-title {
          font-size: 1.3rem;
          font-weight: 600;
          color: #00ffff;
          margin-bottom: 15px;
          text-shadow: 0 0 10px #00ffff;
        }

        .card-cover .card-text {
          color: #ccc;
          font-size: 0.95rem;
          line-height: 1.6;
        }

        @media (max-width: 768px) {
          .card-cover {
            flex-direction: column;
            align-items: center;
          }
        }
      `}</style>
    </div>
  );
}

export default Services;
