import React, { useState } from "react";

export default function Header() {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <header className="fixed top-0 left-0 w-full z-50 bg-gray-900/70 backdrop-blur-md border-b border-gray-800 shadow-md">
      <div className="max-w-7xl mx-auto flex items-center justify-between px-6 md:px-12 py-4 text-white">

        {/* ===== LOGO ===== */}
        <div className="flex items-center gap-3 cursor-pointer group">
          <div className="w-11 h-11 flex items-center justify-center rounded-xl bg-gradient-to-br from-cyan-400 via-indigo-500 to-purple-600 
          font-extrabold text-lg shadow-lg transform transition-all duration-500 group-hover:rotate-180 group-hover:scale-110">
            N
          </div>
          <div className="leading-tight">
            <h1 className="text-xl font-extrabold tracking-wide">Neotech</h1>
            <p className="text-xs text-gray-400">Software & Cloud</p>
          </div>
        </div>

        {/* ===== NAVIGATION (Desktop) ===== */}
        <nav className="hidden md:flex items-center gap-10 text-sm font-medium">
          {["Home", "Services", "About"].map((item) => (
            <a
              key={item}
              href={`#${item.toLowerCase()}`}
              className="relative group"
            >
              {item}
              <span className="absolute left-0 -bottom-1 w-0 h-[2px] bg-cyan-400 group-hover:w-full transition-all duration-300"></span>
            </a>
          ))}
        </nav>

        {/* ===== CONTACT BUTTON ===== */}
        <a
          href="#contact"
          className="hidden md:inline-block px-6 py-2.5 rounded-full bg-gradient-to-r from-cyan-500 to-indigo-600 
          text-white text-sm font-semibold shadow-md hover:shadow-cyan-500/30 hover:scale-105 transition-all duration-300"
        >
          Contact Us
        </a>

        {/* ===== MOBILE MENU TOGGLE ===== */}
        <button
          className="md:hidden flex flex-col gap-1.5 focus:outline-none"
          onClick={() => setMenuOpen(!menuOpen)}
        >
          <span
            className={`block w-6 h-0.5 bg-white transition-all duration-300 ${
              menuOpen ? "rotate-45 translate-y-2" : ""
            }`}
          ></span>
          <span
            className={`block w-6 h-0.5 bg-white transition-all duration-300 ${
              menuOpen ? "opacity-0" : ""
            }`}
          ></span>
          <span
            className={`block w-6 h-0.5 bg-white transition-all duration-300 ${
              menuOpen ? "-rotate-45 -translate-y-2" : ""
            }`}
          ></span>
        </button>
      </div>

      {/* ===== MOBILE MENU ===== */}
      {menuOpen && (
        <div className="md:hidden bg-gray-900/95 backdrop-blur-md text-center text-white border-t border-gray-800">
          {["Home", "Services", "About", "Contact Us"].map((item, idx) => (
            <a
              key={idx}
              href={`#${item.toLowerCase().replace(" ", "")}`}
              className={`block py-3 border-b border-gray-800 hover:bg-gray-800 transition ${
                item === "Contact Us" ? "text-cyan-400 font-semibold" : ""
              }`}
              onClick={() => setMenuOpen(false)}
            >
              {item}
            </a>
          ))}
        </div>
      )}
    </header>
  );
}
