import React, { useState } from "react";

export default function Header() {
  const [open, setOpen] = useState(false);

  return (
    <header className="fixed top-0 left-0 w-full z-50 bg-slate-900/70 backdrop-blur-md border-b border-white/10">
      <div className="max-w-7xl mx-auto px-8 py-4 flex items-center justify-between">
        {/* Logo */}
        <div className="flex items-center gap-2">
          <div className="w-10 h-10 rounded-xl bg-gradient-to-r from-cyan-500 to-blue-600 flex items-center justify-center font-bold text-lg text-white shadow">
            N
          </div>
          <span className="text-xl font-bold tracking-wide text-white">Neotech</span>
        </div>

        {/* Nav Links */}
        <nav className="hidden md:flex gap-8 text-sm font-medium">
          <a href="#home" className="hover:text-cyan-400">Home</a>
          <a href="#services" className="hover:text-cyan-400">Services</a>
          <a href="#about" className="hover:text-cyan-400">About</a>
          <a href="#contact" className="hover:text-cyan-400">Contact</a>
        </nav>

        {/* Contact Button */}
        <a href="#contact" className="hidden md:inline-block bg-gradient-to-r from-cyan-500 to-blue-600 px-5 py-2 rounded-full font-semibold text-white hover:scale-105 transition">
          Contact Us
        </a>

        {/* Mobile menu toggle */}
        <button
          onClick={() => setOpen(!open)}
          className="md:hidden text-white focus:outline-none"
        >
          â˜°
        </button>
      </div>

      {/* Mobile Menu */}
      {open && (
        <div className="md:hidden bg-slate-800/90 backdrop-blur-md text-center space-y-3 py-4">
          <a href="#home" className="block hover:text-cyan-400" onClick={() => setOpen(false)}>Home</a>
          <a href="#services" className="block hover:text-cyan-400" onClick={() => setOpen(false)}>Services</a>
          <a href="#about" className="block hover:text-cyan-400" onClick={() => setOpen(false)}>About</a>
          <a href="#contact" className="block hover:text-cyan-400" onClick={() => setOpen(false)}>Contact</a>
        </div>
      )}
    </header>
  );
}
