import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";

export default function Hero() {
  const slides = [
    "https://img.freepik.com/free-photo/developer-working-with-code-computer_1098-18711.jpg",
    "https://img.freepik.com/free-photo/futuristic-technology-background_53876-100424.jpg",
    "https://img.freepik.com/free-photo/software-engineer-analyzing-code.jpg",
  ];

  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrent((prev) => (prev + 1) % slides.length);
    }, 4000);
    return () => clearInterval(timer);
  }, []);

  return (
    <section
      id="home"
      className="relative min-h-screen flex flex-col md:flex-row items-center justify-between pt-28 bg-gradient-to-br from-blue-50 via-white to-cyan-100 overflow-hidden"
    >
      {/* LEFT CONTENT */}
      <motion.div
        initial={{ x: -80, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ duration: 1 }}
        className="w-full md:w-1/2 px-10 md:px-16"
      >
        <h1 className="text-5xl md:text-6xl font-extrabold text-gray-900 leading-tight">
          Build the Future with{" "}
          <span className="text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-cyan-400">
            NeoTech Solutions
          </span>
        </h1>
        <p className="text-gray-600 mt-6 text-lg">
          We craft intelligent digital experiences that accelerate your business transformation.
        </p>
        <div className="mt-8 flex gap-4">
          <a
            href="#services"
            className="px-8 py-3 bg-gradient-to-r from-blue-500 to-cyan-500 text-white rounded-full font-semibold shadow-md hover:scale-105 transition-transform"
          >
            Get Started
          </a>
          <a
            href="#about"
            className="px-8 py-3 border border-blue-400 text-blue-500 rounded-full hover:bg-blue-50 hover:scale-105 transition-transform"
          >
            Learn More
          </a>
        </div>
      </motion.div>

      {/* RIGHT SLIDESHOW */}
      <div className="relative w-full md:w-1/2 h-[420px] md:h-[480px] overflow-hidden rounded-3xl shadow-2xl mx-10 md:mx-0">
        <AnimatePresence>
          <motion.img
            key={current}
            src={slides[current]}
            alt="slide"
            className="absolute inset-0 w-full h-full object-cover rounded-3xl"
            initial={{ opacity: 0, scale: 1.1 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.95 }}
            transition={{ duration: 1 }}
          />
        </AnimatePresence>
      </div>
    </section>
  );
}
