import React, { useState } from "react";
import { ChevronLeft, ChevronRight } from "lucide-react"; // icons

const slides = [
  {
    id: 1,
    img: "https://images.unsplash.com/photo-1522202176988-66273c2fd55f?auto=format&fit=crop&w=1400&q=80",
    title: "Innovative Software Solutions",
    desc: "We design and develop modern web applications tailored for your business growth.",
  },
  {
    id: 2,
    img: "https://images.unsplash.com/photo-1556761175-5973dc0f32e7?auto=format&fit=crop&w=1400&q=80",
    title: "Empowering Digital Transformation",
    desc: "Delivering reliable, high-performance, and scalable software products.",
  },
  {
    id: 3,
    img: "https://images.unsplash.com/photo-1519389950473-47ba0277781c?auto=format&fit=crop&w=1400&q=80",
    title: "Smart Business Automation",
    desc: "We create tools that enhance productivity and drive efficiency.",
  },
];

function Hero() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const prevSlide = () => {
    setCurrentIndex((prev) =>
      prev === 0 ? slides.length - 1 : prev - 1
    );
  };

  const nextSlide = () => {
    setCurrentIndex((prev) =>
      prev === slides.length - 1 ? 0 : prev + 1
    );
  };

  return (
    <section className="relative w-full h-[85vh] overflow-hidden">
      <img
        src={slides[currentIndex].img}
        alt={slides[currentIndex].title}
        className="w-full h-full object-cover transition-all duration-700"
      />

      {/* Overlay */}
      <div className="absolute inset-0 bg-black/50 flex flex-col justify-center items-center text-center text-white p-6">
        <h1 className="text-4xl md:text-5xl font-bold mb-4">
          {slides[currentIndex].title}
        </h1>
        <p className="max-w-2xl text-lg mb-6">{slides[currentIndex].desc}</p>

        {/* Navigation Buttons */}
        <div className="flex gap-6 mt-4">
          <button
            onClick={prevSlide}
            className="bg-white/20 hover:bg-white/40 p-3 rounded-full"
          >
            <ChevronLeft size={28} />
          </button>
          <button
            onClick={nextSlide}
            className="bg-white/20 hover:bg-white/40 p-3 rounded-full"
          >
            <ChevronRight size={28} />
          </button>
        </div>

        {/* Dots Indicator */}
        <div className="flex mt-6 gap-3">
          {slides.map((_, index) => (
            <span
              key={index}
              className={`w-3 h-3 rounded-full ${
                index === currentIndex ? "bg-white" : "bg-gray-400"
              }`}
            ></span>
          ))}
        </div>
      </div>
    </section>
  );
}

export default Hero;
