import React from "react";

const cards = [
  { title: "Web Apps", desc: "React/Node full-stack apps, SSR, SPAs." },
  { title: "Cloud", desc: "AWS / Azure deployments, infra-as-code." },
  { title: "APIs", desc: "Secure REST & GraphQL APIs." },
  { title: "Mobile", desc: "React Native cross-platform apps." },
  { title: "UI/UX", desc: "Design systems & prototypes." },
  { title: "AI", desc: "Automation, ML integrations." },
];

export default function Services() {
  return (
    <section id="services" className="py-20 bg-white">
      <div className="max-w-7xl mx-auto px-8 md:px-12 text-center">
        <h2 className="text-3xl font-extrabold mb-4">Our Services</h2>
        <p className="text-gray-600 mb-10 max-w-2xl mx-auto">End-to-end software services for startups and enterprises.</p>

        <div className="grid gap-8 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3">
          {cards.map((c, i) => (
            <div key={i} className="p-6 rounded-2xl bg-gradient-to-br from-white to-gray-50 shadow hover:shadow-2xl transition">
              <div className="text-3xl text-cyan-500 font-bold mb-2">{c.title.charAt(0)}</div>
              <h3 className="text-lg font-semibold mb-2">{c.title}</h3>
              <p className="text-gray-600">{c.desc}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
